ELSEIF(TG_OP = 'INSERT') THEN
INSERT INTO auditar.AUDITORIA ("NombreTabla","Operacion","ValorAnterior","NuevoValor","UpdateDate","USUARIO")
VALUES (TG_TABLE_NAME,'I',NULL,row_to_json(NEW.*),NOW(),USER);
RETURN NEW;
END IF;
RETURN NULL;
end
$$
  LANGUAGE plpgsql VOLATILE 
  COST 100;

